mapper.select:
select

from syspower
where level = #{level,jdbcType=INTEGER} and lft<#{lft,jdbcType=INTEGER} and rgt>#{rgt,jdbcType=INTEGER}

select

from syspower
where level = #{level,jdbcType=INTEGER} and lft<#{lft,jdbcType=INTEGER}
and lft>#{plft,jdbcType=INTEGER}
and rgt<#{prgt,jdbcType=INTEGER}
order by lft desc limit 1

select

from syspower
where level = #{level,jdbcType=INTEGER} and lft>#{lft,jdbcType=INTEGER}
and lft>#{plft,jdbcType=INTEGER}

	and rgt>#{prgt,jdbcType=INTEGER}


	and rgt<#{prgt,jdbcType=INTEGER}

order by lft asc limit 1

mapper.delete:
delete from syspower where lft>=#{lft,jdbcType=INTEGER} and rgt<=#{rgt,jdbcType=INTEGER};
update syspower set
lft=lft-(#{rgt,jdbcType=INTEGER}-#{lft,jdbcType=INTEGER}+1) where lft>#{lft,jdbcType=INTEGER};
update syspower set
rgt=rgt-(#{rgt,jdbcType=INTEGER}-#{lft,jdbcType=INTEGER}+1) where rgt>#{rgt,jdbcType=INTEGER};
